import React, { useEffect, useState } from "react";
import Modal from "../Modal/Modal";

export default function ProjectItem(props) {
  const [show, setShow] = useState(false);
  let [imageIndex, setImageIndex] = useState(0);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const showModal = (e) => {
    setShow(true);
  };

  const project = props.project;
  useEffect(() => {
    const interval = setInterval(() => {
      // const index = imageIndex + 1;
      if (!project.images) {
        return;
      }
      console.log(imageIndex, "===", project.images.length);
      if (imageIndex >= project.images.length - 1) {
        console.log("Hello");
        setImageIndex(0);
      } else {
        setImageIndex(imageIndex++);
        console.log("Hello else");
      }
    }, 2000);
    return () => {
      clearInterval(interval);
      console.log("unmount");
    };
  });

  console.log(project.images);
  return (
    <li key={project.title}>
      <div className="project-tile">
        <figure>
          <div class="w3-content w3-section">
            {project.images.map((image, index) => (
              <img
                className={
                  "mySlides " + (imageIndex == index ? "block" : "none")
                }
                src={image}
              />
            ))}
          </div>
          <img
            src={project.image}
            alt={project.title}
            onClick={(e) => {
              setDescription(project.description);
              setTitle(project.title);
              showModal();
            }}
          />
          <figcaption>
            <h3>{project.title}</h3>
            <div className="tools-used">{project.toolsUsed.join(" | ")}</div>
          </figcaption>
          <section className="links">
            <div className="source">
              <span className="fas fa-code fa-fw project-link-icon"></span>
              <a href={project.source} target="_blank" rel="noreferrer">
                <p> Source Code</p>
              </a>
            </div>
            {project.backEnd ? (
              <div className="server">
                <span className="fas fa-server fa-fw project-link-icon"></span>
                <a href={project.backEnd} target="_blank" rel="noreferrer">
                  <p> Server Code</p>
                </a>
              </div>
            ) : null}
            <div className="server"></div>
            <div className="demo">
              <span className="fas fa-desktop fa-fw project-link-icon"></span>
              <a href={project.url} target="_blank" rel="noreferrer">
                <p> Live Demo</p>
              </a>
            </div>
          </section>
        </figure>

        <Modal
          show={show}
          setShow={setShow}
          title={title}
          description={description}
        />
      </div>
    </li>
  );
}
