import React, { useEffect, useState } from "react";
import Modal from "../Modal/Modal";
import ProjectImage from "../ProjectImage/ProjectImage";

export default function ProjectItem(props) {
  let [imageIndex, setImageIndex] = useState(0);

  const project = props.project;
  useEffect(() => {
    console.log("mount");
    const interval = setInterval(() => {
      // const index = imageIndex + 1;
      if (!project.images) {
        return;
      }
      // console.log(imageIndex, "===", project.images.length);
      if (imageIndex >= project.images.length) {
        // console.log("Hello");
        setImageIndex(0);
      } else {
        setImageIndex(imageIndex++);
        // console.log("Hello else");
      }
    }, 2000);
    return () => {
      clearInterval(interval);
      console.log("unmount");
    };
  });

  console.log(project.images);
  return (
    <li key={project.title}>
      <div className="project-tile">
        <figure>
          <div class="w3-content w3-section">
            {project.images.map((image, index) => (
              <ProjectImage
                project={project}
                image={image}
                index={index}
                imageIndex={imageIndex}
              />
            ))}
          </div>
          {/* <img
            src={project.image}
            alt={project.title}
            onClick={(e) => {
              setDescription(project.description);
              setTitle(project.title);
              showModal();
            }}
          /> */}
          <figcaption>
            <h3>{project.title}</h3>
            <div className="tools-used">{project.toolsUsed.join(" | ")}</div>
          </figcaption>
          <section className="links">
            <div className="source">
              <span className="fas fa-code fa-fw project-link-icon"></span>
              <a
                href={project.source}
                target="_blank"
                rel="noopener noreferrer"
              >
                <p> Source Code</p>
              </a>
            </div>
            {project.backEnd ? (
              <div className="server">
                <span className="fas fa-server fa-fw project-link-icon"></span>
                <a
                  href={project.backEnd}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <p> Server Code</p>
                </a>
              </div>
            ) : null}
            <div className="server"></div>
            <div className="demo">
              <span className="fas fa-desktop fa-fw project-link-icon"></span>
              <a href={project.url} target="_blank" rel="noopener noreferrer">
                <p> Live Demo</p>
              </a>
            </div>
          </section>
        </figure>

        <Modal
          show={show}
          setShow={setShow}
          title={title}
          description={description}
        />
      </div>
    </li>
  );
}
